func TestHandler_Fetch(t *testing.T) {
	t.Parallel()
	mockXML := `<?xml version="1.0" encoding="UTF-8"?>
<rss version="2.0" xmlns:dc="http://purl.org/dc/elements/1.1/" xmlns:media="http://search.yahoo.com/mrss">
  <channel>
    <title>We Work Remotely: Remote jobs in design, programming, marketing and more</title>
    <link>https://weworkremotely.com/remote-jobs.rss</link>
    <description>We Work Remotely: Remote jobs in design, programming, marketing and more</description>
    <language>en-US</language>
    <ttl>60</ttl>
    <item>
      <media:content url="https://wwr-pro.s3.amazonaws.com/logos/0064/7708/logo.gif" type="image/png"/>
      <title>Credit Repair Cloud: Product Manager</title>
      <region>Anywhere (100% Remote) Only</region>
      <category>Product</category>
      <type>Full-Time</type>
      <description>&lt;img src="https://we-work-remotely.imgix.net/logos/0064/7708/logo.gif?ixlib=rails-4.0.0&amp;w=50&amp;h=50&amp;dpr=2&amp;fit=fill&amp;auto=compress" /&gt;

&lt;p&gt;
  &lt;strong&gt;Headquarters:&lt;/strong&gt; Los Angeles, CA
    &lt;br /&gt;&lt;strong&gt;URL:&lt;/strong&gt; &lt;a href="https://www.creditrepaircloud.com/careers"&gt;https://www.creditrepaircloud.com/careers&lt;/a&gt;
&lt;/p&gt;

&lt;div&gt;&lt;strong&gt;Why Credit Repair Cloud&lt;/strong&gt;&lt;/div&gt;&lt;div&gt;Wondering why we think you’ll love working at Credit Repair Cloud? Here are 5 reasons!&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;Stability &amp;amp; growth&lt;/strong&gt;&lt;/div&gt;&lt;div&gt;In 2013, Daniel Rosen launched Credit Repair Cloud, the software that now powers the credit repair industry. You will be joining a stable yet fast-growing, purpose-driven, self-funded organization.  &lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;Talent will surround you&lt;/strong&gt;&lt;/div&gt;&lt;div&gt;We have created a highly collaborative environment where everyone has a real chance to learn and grow. Your teammates are always willing to help and share their knowledge. We also encourage you to share your ideas!&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;Our culture and values&lt;/strong&gt;&lt;/div&gt;&lt;div&gt;Our approach to hiring and working as a global team relies on valuing every individual for who they are, their belief in our movement, and their zone of genius superpowers!&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;Results-oriented team&lt;/strong&gt;&lt;/div&gt;&lt;div&gt;We don’t micromanage, and we try not to interrupt your work with random tasks. We trust our team members to take full responsibility and ownership for the work that they do.  &lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;You can pick where you want to work every day&lt;/strong&gt;&lt;/div&gt;&lt;div&gt;At Credit Repair Cloud, we embrace the remote culture. Some of our team works from our offices while the others are spread around the world. Every day you get to work in the place that makes you most productive.&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;What we offer&lt;/strong&gt;&lt;/div&gt;&lt;ul&gt;
&lt;li&gt;Competitive salary.&lt;/li&gt;
&lt;li&gt;The opportunity to attend our Credit Repair Expo.&lt;/li&gt;
&lt;li&gt;Unlimited PTO. Take time off when you need it. We trust you!&lt;/li&gt;
&lt;li&gt;Paid Parental Leave.&lt;/li&gt;
&lt;li&gt;Paid conferences.  We are big fans of continued growth.&lt;/li&gt;
&lt;li&gt;Annual benefits, which may include a budget for personal development.&lt;/li&gt;
&lt;li&gt;Profit share bonus plan.&lt;/li&gt;
&lt;li&gt;Free access to Nutrition, Yoga, Mindfulness, and Fitness classes.&lt;/li&gt;
&lt;li&gt;Employee Wellness Program.&lt;/li&gt;
&lt;li&gt;Fun perks &amp;amp; monthly activities.&lt;/li&gt;
&lt;li&gt;A comprehensive and paid onboarding process.&lt;/li&gt;
&lt;li&gt;The freedom to work from anywhere around the world.&lt;/li&gt;
&lt;/ul&gt;&lt;div&gt;Now that you know us let’s talk about you!&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;Role&lt;/strong&gt;&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;You will be a vital part of our product development team, working closely with the development, design, customer success, and leadership teams, to build fast-evolving and unique products. You will be involved in all stages of product development and will have a real impact on delivering products that help our customers streamline their process to starting, running, and growing their business.&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/div&gt;&lt;ul&gt;
&lt;li&gt;Fully understand Credit Repair Cloud’s business strategy and work to deliver products with our unique mission and perspective in mind. &lt;/li&gt;
&lt;li&gt;Fully understand our Customer Journey, from first exposure and into an active paid user and beyond.&lt;/li&gt;
&lt;li&gt;Be a positive and encouraging voice for our team, according to our &lt;a href="https://drive.google.com/a/creditrepaircloud.com/open?id=1VIv9GEvudaQqg7Sj-r687JUNAuZkdzwPDjRuJFmtT0g"&gt;Company Culture Code&lt;/a&gt;.&lt;/li&gt;
&lt;li&gt;Accountable for creating the end-end flow of features in all aspects of product management. &lt;/li&gt;
&lt;li&gt;You will closely work with a small team of UX/UI designers and an engineering team to execute this vision.&lt;/li&gt;
&lt;li&gt;Collaborate with the engineering team and set goals to be in line with the scope of the feature/product design and to mitigate the risk of any bugs deployed into the production. &lt;/li&gt;
&lt;li&gt;Conducting market research analysis of competitors/various SaaS businesses. &lt;/li&gt;
&lt;li&gt;Should be able to create User Stories with supporting documentation in order for the Product Owner and the dev team to develop User Stories and help define Acceptance Criteria. &lt;/li&gt;
&lt;li&gt;Deliver world-class software, analyze results from app changes, and suggest improvements.&lt;br&gt; &lt;/li&gt;
&lt;/ul&gt;&lt;div&gt;We're an agile, fast-growing company, and this job description isn't meant to be a complete list of your qualifications or all of the things you'll do, but to give you an idea of your daily work here at Credit Repair Cloud. &lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;strong&gt;Requirements: &lt;/strong&gt;&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;ul&gt;
&lt;li&gt;3+ years of experience working as a full-time Product Manager in a SaaS company, delivering complex products. &lt;/li&gt;
&lt;li&gt;Good understanding of working with API’s.&lt;/li&gt;
&lt;li&gt;Excellent verbal and written communication skills, with proven experience in presenting ideas to key contributors throughout an organization.&lt;/li&gt;
&lt;li&gt;Experience defining and helping develop new feature sets from inception to launch. &lt;/li&gt;
&lt;li&gt;Prior experience working with Engineering teams and UI/UX designers.&lt;/li&gt;
&lt;li&gt;Prior experience working with remote teams. &lt;/li&gt;
&lt;li&gt;Good understanding of Agile methodologies in a Scrum environment. &lt;/li&gt;
&lt;/ul&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;
&lt;strong&gt;Interested?&lt;/strong&gt; Click &lt;strong&gt;Apply to this job&lt;/strong&gt; to join the movement! &lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;em&gt;Credit Repair Cloud is an Equal Opportunity Employer and is committed to working with a diverse staff. We hire globally for talent and drive and value members by work deliverables and passion. No matter what race, color, creed, religion, gender, gender identity, gender expression, national origin, citizenship, age, sex, sexual orientation, pregnancy, marital status, ancestry, physical or mental disability, military or veteran status, or any other characteristic protected by law, we are all equal here.&lt;/em&gt;&lt;/div&gt;&lt;div&gt; &lt;/div&gt;&lt;div&gt;&lt;em&gt;We encourage every talented and qualified person to apply. In your application, please feel free to note which pronouns you use (For example - she/her/hers, he/him/his, they/them/theirs, etc.)&lt;/em&gt;&lt;/div&gt;&lt;div&gt;
&lt;br&gt;&lt;br&gt;
&lt;/div&gt;

&lt;p&gt;&lt;strong&gt;To apply:&lt;/strong&gt; &lt;a href="https://weworkremotely.com/remote-jobs/credit-repair-cloud-product-manager-1"&gt;https://weworkremotely.com/remote-jobs/credit-repair-cloud-product-manager-1&lt;/a&gt;&lt;/p&gt;
</description>
      <pubDate>Fri, 30 Jul 2021 23:17:55 +0000</pubDate>
      <expires_at>Sun, 29 Aug 2021 23:17:55 +0000</expires_at>
      <guid>https://weworkremotely.com/remote-jobs/credit-repair-cloud-product-manager-1</guid>
      <link>https://weworkremotely.com/remote-jobs/credit-repair-cloud-product-manager-1</link>
    </item>
</channel>
</rss>`
	res := &http.Response{
		Body:       ioutil.NopCloser(strings.NewReader(mockXML)),
		StatusCode: http.StatusOK,
	}
	handler := NewHandler(mockClient(res, nil))
	jbs, err := handler.Fetch(context.Background())
	assert.Len(t, jbs, 1)
	assert.Nil(t, err)
	assert.Equal(t, "Full-Time", jbs[0].JobType())
	assert.Equal(t, "Product Manager", jbs[0].Role())
	assert.Equal(t, "Credit Repair Cloud", jbs[0].CompanyName())
	assert.Equal(t, "https://weworkremotely.com/company/credit-repair-cloud", jbs[0].CompanyURL())
	assert.Equal(t, "weworkremotely", jbs[0].Source())
}

func mockClient(res *http.Response, err error) worker2.IHTTPClient {
	transport := &worker2.MockRoundTripper{}
	transport.On("RoundTrip", mock.Anything).
		Return(res, err)
	cli := &http.Client{}
	cli.Transport = transport
	return wrapper.NewHTTPClient(cli)
}

func TestHandler_FetchFail(t *testing.T) {
	t.Parallel()
	res := &http.Response{
		Body:       ioutil.NopCloser(strings.NewReader(``)),
		StatusCode: http.StatusInternalServerError,
	}
	handler := NewHandler(mockClient(res, nil))
	jbs, err := handler.Fetch(context.Background())
	assert.Nil(t, jbs)
	assert.NotNil(t, err)
}

func TestHandler_FetchJobFail(t *testing.T) {
	t.Parallel()
	agent := &worker2.MockHTTPClient{}
	agent.On("Do", mock.Anything).
		Return(&http.Response{
			Body:       io.NopCloser(strings.NewReader(``)),
			StatusCode: http.StatusNotFound,
		}, nil)
	handler := NewHandler(agent)
	jb, _ := job.NewJob(uuid.New(), "-", "-", "-", "-", "https://weworkremotely.com/remote-jobs/random-link", "-", "-", []string{})
	jbs, err := handler.FetchJob(context.Background(), jb)
	assert.Nil(t, jbs)
	assert.NotNil(t, err)
}
